import React, { useState } from "react";
import { Container, ListGroup, ListGroupItem, Button } from "reactstrap";
import { CSSTransition, TransitionGroup } from "react-transition-group";
import uuid from "uuid";

export default function TopicList() {
  const [topics, setTopics] = useState([
    { id: uuid(), name: "Ethereum", createdAt: new Date() },
    { id: uuid(), name: "Lightening", createdAt: new Date() },
    { id: uuid(), name: "BTCPay Server", createdAt: new Date() },
    { id: uuid(), name: "JAMStack", createdAt: new Date() },
    { id: uuid(), name: "Graphql", createdAt: new Date() }
  ]);
  console.log(topics);
  return (
    <Container>
      <Button
        color="dark"
        style={{ marginBottom: "2rem" }}
        onClick={() => {
          const name = prompt("Enter Topic");
          if (name) {
            setTopics([...topics, name]);
          }
        }}
      >
        Add Topic
      </Button>
      <ListGroup>
        <TransitionGroup className="topic-list">
          {topics.map(({ id, name, createdAt }) => {
            console.log(name);
            return (
              <CSSTransition key={id} timeout={500} className="fade">
                <ListGroupItem>{name}</ListGroupItem>
              </CSSTransition>
            );
          })}
        </TransitionGroup>
      </ListGroup>
    </Container>
  );
}
